        const area = window.innerWidth * window.innerHeight;
        const baseDensity = prefersReduced ? 0.00004 : 0.00012; // æ¯åƒç´ å¯†åº¦
        const count = Math.max(80, Math.min(280, Math.floor(area * baseDensity)));
        for (let i = 0; i < count; i++) {
          const d = document.createElement('div');
          d.className = 'raindrop';
          const left = rand(0, 100);
          const delay = rand(0, 1200);
          const dur = rand(800, 1400);
          const op = rand(0.45, 0.95);
          const wind = rand(8, 26);
          d.style.left = left + 'vw';
          d.style.setProperty('--delay', delay + 'ms');
          d.style.setProperty('--dur', dur + 'ms');
          d.style.setProperty('--o', op);
          d.style.setProperty('--wind', wind + 'px');
          el.appendChild(d);
        }
      }

      function createSnow(el) {
        const area = window.innerWidth * window.innerHeight;
        const baseDensity = prefersReduced ? 0.00002 : 0.00006;
        const count = Math.max(50, Math.min(200, Math.floor(area * baseDensity)));
        for (let i = 0; i < count; i++) {
          const s = document.createElement('div');
          s.className = 'snowflake';
          const left = rand(0, 100);
          const delay = rand(0, 8000);
          const dur = rand(8000, 16000);
          const op = rand(0.65, 1);
          const drift = rand(-40, 40);
          s.style.left = left + 'vw';
          s.style.setProperty('--delay', delay + 'ms');
          s.style.setProperty('--dur', dur + 'ms');
          s.style.setProperty('--o', op);
          s.style.setProperty('--drift', drift + 'px');
          el.appendChild(s);
        }
      }

      function createWind(el) {
        const area = window.innerWidth * window.innerHeight;
        const baseDensity = prefersReduced ? 0.00001 : 0.00003;
        const count = Math.max(30, Math.min(160, Math.floor(area * baseDensity)));
        for (let i = 0; i < count; i++) {
          const leaf = document.createElement('div');
          leaf.className = 'leaf';
          const startY = rand(0, 90);
          const delay = rand(0, 4000);
          const dur = rand(4500, 9000);
          const o = rand(0.55, 0.95);
          leaf.style.top = startY + 'vh';
          leaf.style.setProperty('--startY', startY + 'vh');
          leaf.style.setProperty('--delay', delay + 'ms');
          leaf.style.setProperty('--dur', dur + 'ms');
          leaf.style.setProperty('--o', o);
          leaf.style.transform = `translate3d(-10vw, 0, 0)`;
          el.appendChild(leaf);
        }
      }

      function renderBackground(weather) {
        const next = useAVisible ? canvasB : canvasA; // å–ç›¸åå±‚ä½œä¸ºä¸‹ä¸€ä¸ª
        clearCanvas(next);
        next.className = 'bg-canvas'; // é‡ç½®çŠ¶æ€

        // åˆ›å»ºç‰¹å®šå¤©æ°”èƒŒæ™¯
        if (weather === 'sunny') createSunny(next);
        if (weather === 'rain') createRain(next);
        if (weather === 'snow') createSnow(next);
        if (weather === 'wind') createWind(next);

        // ç«‹å³æ˜¾ç¤ºï¼ˆä¸‹ä¸€å¸§åˆ‡æ¢å¯è§ï¼‰
        requestAnimationFrame(() => {
          // å…ˆæŠŠ next è®¾ä¸ºå¯è§
          next.classList.add('visible');
          // å†æŠŠ prev æ·¡å‡º
          const prev = useAVisible ? canvasA : canvasB;
          prev.classList.remove('visible');
          useAVisible = !useAVisible;
        });
      }

      function setWeather(weather) {
        active = weather;
        setTheme(weather);
        setActiveButton(weather);
        populateInfo(weather);
        renderBackground(weather);
      }

      btns.forEach(btn => {
        btn.addEventListener('click', () => setWeather(btn.dataset.weather));
        btn.addEventListener('keydown', (e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault(); setWeather(btn.dataset.weather);
          }
        });
      });

      refreshBtn.addEventListener('click', () => populateInfo(active));

      // åˆå§‹åŒ–ï¼šæ ¹æ®çª—å£å¤§å°é¢„æ¸²æŸ“å½“å‰èƒŒæ™¯
      function init() { setWeather('sunny'); }
      init();

      // å“åº”å¼ï¼šçª—å£å°ºå¯¸å˜åŒ–æ—¶ï¼Œé‡ç»˜å½“å‰èƒŒæ™¯ï¼ˆé˜²æ­¢ç²’å­å¯†åº¦ä¸åŒ¹é…ï¼‰
      let resizeTimer = 0;
      window.addEventListener('resize', () => {
        clearTimeout(resizeTimer);
        resizeTimer = window.setTimeout(() => renderBackground(active), 200);
      });
    })();
  </script>
